import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import './App.css';

function App() {
    const [id, setId] = useState('');
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [color, setColor] = useState('#ffee9b');
    const [notes, setNotes] = useState([]);

    useEffect(() => {
        Axios.get('http://localhost:3001/api/read')
        .then((response) => {
            setNotes(response.data.records);
        });
    }, []);

    const submitForm = () => {
        if (id) {
            Axios.put('http://localhost:3001/api/update', {id: id, title: title, description: description, color: color})
            .then((response) => {
                console.log(response.data);
            });
        } else {
            Axios.post('http://localhost:3001/api/create', {title: title, description: description, color: color})
            .then((response) => {
                console.log(response.data);
            });
        }
    };

    const updateForm = (note) => {
        setId(note.id);
        setTitle(note.title);
        setDescription(note.description);
        setColor(note.color);
    };

    const clearForm = () => {
        setId('');
        setTitle('');
        setDescription('');
        setColor('#ffee9b');
    };

    return (
        <div className="App">
            <div className="wrapper" id="form">
                <input type="text" name="title" placeholder="Title" autoComplete="off" value={{title}} onChange={(e) => { setTitle(e.target.value) }} />
                <textarea name="description" placeholder="Write somethign here...." autoComplete="off" value={{description}} onChange={(e) => { setDescription(e.target.value) }}></textarea>
                <div style={{marginBottom: "20px"}}>
                    <span>
                        <input type="radio" name="color" value="#ffee9b" onChange={(e) => { setColor(e.target.value) }} defaultChecked={color=='#ffee9b' || color=='' ? true : false} />
                        <input type="radio" name="color" value="#b2d4f5" onChange={(e) => { setColor(e.target.value) }} defaultChecked={color=='#b2d4f5' ? true : false} />
                        <input type="radio" name="color" value="#fcc3a7" onChange={(e) => { setColor(e.target.value) }} defaultChecked={color=='#fcc3a7' ? true : false} />
                        <input type="radio" name="color" value="#8fd1bb" onChange={(e) => { setColor(e.target.value) }} defaultChecked={color=='#8fd1bb' ? true : false} />
                        <input type="radio" name="color" value="#d5dadc" onChange={(e) => { setColor(e.target.value) }} defaultChecked={color=='#d5dadc' ? true : false} />
                    </span>
                    <span>
                        <button style={{color: "rgba(0, 0, 0, 0.5)"}} onClick={clearForm}>CLEAR</button>
                        <button className="submit" onClick={submitForm}>SAVE</button>
                    </span>
                </div>
            </div>
            {notes.map((data) => {
                return (
                    <div className="wrapper" style={{backgroundColor: data.color}} key={data.id}>
                        <p className="title">{data.title}</p>
                        <p className="description">{data.description}</p>
                        <div>
                            <small>{data.updated_at}</small>
                            <span>
                                <button onClick={() => updateForm(data)}>EDIT</button>
                                <button style={{color: "#ff0000"}}>DELETE</button>
                            </span>
                        </div>
                    </div>
                );
            })}
        </div>
    );
}

export default App;
